import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Section = styled.section`
  position: relative;
  border: 1px solid black;
  width: 60%;
  min-height: 400px;
  display: flex;
  flex-direction: column;
  align-items: center;
  // justify-content: center;
  background-color: #66B093;
  color: white;
`;
const SectionUnderlay = styled.div`
  position: absolute;
  border: 1px solid rebeccapurple;
  z-index: -2;
  left: -10px;
  top 10px;
  width: 100%;
  height: 100%;
  background-color: rebeccapurple;
  border-radius: 5px 10px 5px 10px;
`;
const Header = styled.header`
  display: flex;
  flex-direction: column;
  align-items: center;
  border-bottom: 1px solid grey;
`;
const H3 = styled.h3`
  font-size: 20px;
`;
const P = styled.p`
  width: 80%;
  font-size: 18px;
  line-height: 30px;
`;

const About = () => {
  return (
    <Section>
      <Header>
        <H3>About Me</H3>
      </Header>
      <P>
        Software engineer with extensive experience learning new things quickly.  Expert Google-er and voracious student of the docs.  React developer, architect of RESTful APIs, database schema design and optimization specialist.  Proficient in English and JavaScript, currently learning TypeScript.  Eager to learn and happy to teach.
      </P>
    </Section>
  );
};

export default About;
