import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Section = styled.section`
  margin-top: 100px;
  position: relative;
  border: 1px solid black;
  width: 60%;
  min-height: 350px;
  padding-bottom: 10px;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: white;
  box-shadow: -10px 10px 0px 1px ${({ theme }) => theme.colors.sandDollar};
`;
const Header = styled.header`
  display: flex;
  flex-direction: column;
  align-items: center;
  border-bottom: 1px solid grey;
  padding-top: 5px;
`;
const H3 = styled.h3`
  font-size: 20px;
`;
const P = styled.p`
  width: 80%;
  font-size: 18px;
  line-height: 30px;
`;

const About = () => {
  return (
    <Section>
      <Header>
        <H3>About Me</H3>
      </Header>
      <P>
        Software engineer with extensive experience learning new things quickly  React developer, architect of RESTful APIs, database schema designer, and search engine optimization specialist.  Advocate of human-readable code and user-centered design.  Proficient in English and JavaScript, currently learning TypeScript.
      </P>
      <P>
        Let's make something cool.
      </P>
    </Section>
  );
};

export default About;
