// import { urlObjectKeys } from 'next/dist/next-server/lib/utils';
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Overlay = styled.div`
  z-index: 1;
  position: absolute;
  height: 100%;
  width: 100%;
  display: none;
  align-items: center;
  justify-content: center;
`;
const OverlayBackground = styled.div`
  z-index: -1;
  position: absolute;
  flex: 1;
  height: 100%;
  width: 100%;
  background-color: darkgrey;
  opacity: 60%;
`;
const OverlayForeground = styled.div`
  z-index: 0;
  background-color: black;
  color: white;
  height: 60%;
  width: 80%;
  padding-left: 3%;
  padding-right: 3%;
  padding-bottom: 3%;
  font-family: 'Noto Sans JP', sans-serif;
  font-weight: 100;
  font-size: 14px;
  overflow: scroll;
`;

const Container = styled.div`
  border-radius: 5px 10px 5px 10px;
  position: relative;
  display: flex;
  background-color: white;
  flex-direction: column;
  align-items: center;
  width: 25%;
  min-height: 425px;
  border: 1px solid grey;
  &:hover ${Overlay} {
    display: flex;
  }
`;
const Image = styled.img`
  object-fit: scale-down;
  width: 95%;
`;
const Footer = styled.footer`
  background-color: white;
  border-top: 1px solid grey;
  width: 100%;
  flex: 1;
  display: flex;
  flex-direction: column;
  border-bottom-left-radius: 10px;
  border-bottom-right-radius: 5px;
`;
const H5 = styled.h5`
  font-size: 16px;
  font-weight: light;
  margin-bottom: 1%;
  line-height: 0px;
  // text-align:center;
`;
const Info = styled.span`
  display: flex;
  flex: 1;
  flex-direction: column;
  margin-left: 10px;
  margin-right: 10px;
`;
const Stack = styled.p`
  font-size: 13px;
`

const Project = ({ project, index, theme }) => {
  console.log('PROJECT', theme)
  const ContainerUnderlay = styled.div`
    position: absolute;
    // border: 1px solid rebeccapurple;
    z-index: -2;
    left: -10px;
    top 10px;
    width: 100%;
    height: 100%;
    background-color: ${({ theme }) => Object.values(theme.colors)[index]};
    border-radius: 5px 10px 5px 10px;
  `;
  return (
    // <Container style={{backgroundImage: `url('${project.img}')`}}>
    <Container>
    <ContainerUnderlay/>
      <Overlay>
        <OverlayBackground />
        <OverlayForeground>
          <p>{project.desc}</p>
        </OverlayForeground>
      </Overlay>
      <Image src={`${project.img}`}></Image>
      <Footer>
        <Info>
          <H5>{project.name}</H5>
          <Stack><b>Tech:</b> {project.techStack}</Stack>
        </Info>
      </Footer>
    </Container>
  );
};

export default Project;
